import React, { useState, useEffect } from 'react'
import ReviewCard from './ReviewCard'
import { getReviews } from '../actions/reviews'
import { connect } from 'react-redux'
import { withRouter } from 'react-router-dom'

const ReviewContainer = ({getReviews, reviewId, reviews }) => {
  const [ isLoading, setIsLoading ] = useState(false)
  const [ review, setReview] = useState({})
  useEffect(() => {
    setIsLoading(true);
    getReviews()
    setIsLoading(false)
  }, [getReviews, reviewId, reviews])
  return isLoading ? <div>Loading</div> : <ReviewCard review={reviews.find(review => review.id === reviewId)} />
}


const mapStateToProps = (state, ownProps) => {
  const reviewId = ownProps.match.params.id
  return ({
    reviews : state.reviews,
    reviewId
  })
}


export default withRouter(connect(mapStateToProps, {geReviews})(ReviewContainer))
