import {Link} from 'react-router-dom'
import { connect } from 'react-redux'
import { getConcerts } from '../actions/concerts.js'
import { getConcertReviews } from '../actions/reviews.js'


class ConcertCard extends React.Component {



  componentDidMount(){
      this.props.getConcerts()
      this.props.concert && this.props.getConcertReviews(this.props.concert)
  }


  comp

