    const { concert, userId } = this.props
    console.log(concert)
    return (
      concert ?
        <div className = "ConcertCard">
          <h3>{concert.attributes.name}</h3>
          <p>Date: {concert.attributes.date}</p>
          <p>Artist: {concert.attributes.artist}</p>
          <p>Venue: {concert.attributes.venue}</p>
          <p>Aggregated Score:{concert.attributes.combined_review_score}</p>
          <Link to ={`/concerts/${concert.id}/reviews`}> See all reviews for this concert</Link><br/>
          {!!concert.attributes.reviews.find(rev => rev.user_id == userId) ? <Link to={`/reviews/${concert.attributes.reviews.find(rev => rev.user_id == userId).id}`}>Your Review</Link>: <Link to={`/concerts/${concert.id}/reviews/new`}>Add a review</Link> }


          </div> : <div> Maaaaan they ain't no concert here </div>
      )

    }
  }

const mapStateToProps = state => {
  const userId = state.currentUser ? state.currentUser.id : ""
  return ({
    userId,

  })
}


export default connect(mapStateToProps, {getConcerts, getConcertReviews})(ConcertCard)
