import React from 'react'
import {Link} from 'react-router-dom'
import { connect } from 'react-redux'
import { getConcerts, getConcert } from '../actions/concerts.js'
import { getConcertReviews } from '../actions/reviews.js'


class ConcertCard extends React.Component {

  componentDidMount(){
    this.props.concert && this.props.getConcerts(this.props.concert.lineup[0]) && this.props.getConcert(this.props.concert)
  }



  render () {
    const { concert, userId, saveConcert, fetchedConcert } = this.props
    return (
      concert ?
        <div className = "ConcertCard">
          <p>Date: {concert.datetime}</p>
          <p>Artist: {concert.lineup[0]}</p>
          <p>Venue: {concert.venue.name}</p>
          <p>Aggregated Score: {fetchedConcert.attributes.combined_review_score}</p>
          <Link to ={`/concerts/${concert.id}/reviews`}> See all reviews for this concert</Link><br/>
          {!!concert.reviews.find(rev => rev.attributes.user.id == userId) ? <Link to={`/reviews/${concert.reviews.find(rev => rev.attributes.user.id == userId)}`}>Your Review</Link>: <Link to={`/concerts/${concert.id}/reviews/new`}>Add a review</Link> }


          </div> : <div> Maaaaan they ain't no concert here </div>
      )

    }
  }

const mapStateToProps = state => {
  const userId = state.currentUser ? state.currentUser.id : ""
  return ({
    userId,
    concerts: state.concerts,
    reviews: state.reviews,
    fetchedConcert: state.concert

  })
}


export default connect(mapStateToProps, {getConcerts, getConcertReviews, getConcert})(ConcertCard)
