import axios from 'axios';
import store from './store';

export const setConcerts = concerts => {
  return {
    type: "SET_CONCERTS",
    concerts
  }
}

export const clearConcerts = () => {
  return {
    type: "CLEAR_CONCERTS"
  }
}

const REACT_APP_SEATGEEK_CLIENT_ID = process.env.REACT_APP_SEATGEEK_CLIENT_ID ;

export const getConcerts = (query) => {
  return (dispatch) => {
    dispatch({ type: 'LOADING_CONCERTS' });
    return axios.get("https://rest.bandsintown.com/artists/Kishi%20Bashi/events?app_id=73b689174c8cd3f299a050e1c75f57a5&date=past")
      .then(concerts => {
          dispatch({ type: 'GET_CONCERTS', concerts })
          dispatch(addReviewsToConcerts(concerts))})
    }
    }


export const addReviewsToConcerts = (concerts) => {
  return (dispatch, getState) => {
    const { reviews } = getState().reviews
    return concerts.data.map(concert => concert.reviews = reviews.filter(review => review.concert.id === concert.id))
    .then(theConcerts => {dispatch({ type: 'ADD_REVIEWS_TO_CONCERTS', concerts })})
}
}
