import React from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { withRouter } from 'react-router-dom'
import { saveConcert } from '../.././actions/concerts/concerts'
import Card from 'react-bootstrap/Card'
import CardDeck from 'react-bootstrap/CardDeck'



const ConcertsList = ({concerts, saveConcert}) => {

    const date = (datetime) => {
      return new Date(datetime).toDateString()
    }

    return (

      <div className = "concerts-list">

        {concerts.sort((a, b) => (a.datetime < b.datetime) ? 1 : -1).map(c => <CardGroup><Card key={c.id} to={`/concerts/${c.id}`}  onClick={()=>saveConcert(c)}>{c.lineup[0]} at {c.venue.name} on {date(c.datetime)}</Card></CardGroup>)}
      </div>
    )
}



const mapStateToProps = (state, ownProps) => {
  return {

      concerts: state.concerts

  }
}

export default withRouter(connect(mapStateToProps, {saveConcert})(ConcertsList))
