import React from 'react'
import {Link} from 'react-router-dom'
import { connect } from 'react-redux'
import { withRouter} from 'react-router-dom'
import { getUserReviews } from '../actions/reviews'


const ReviewCard = ({ review, userId, ownProps, getUserReviews }) => {


  return (
    review ?
      <div>
        <h3>{review.user.username}'s Review for <Link to={`/concerts/${review.attributes.concert.id}`}>{review.attributes.concert.name}</Link></h3>
        <p>Performance: {review.set_score}</p>
        <p>Venue: {review.venue_score}</p>
        <p>Sound: {review.sound_score}</p>
        <p>Set: {review.set_score}</p>
        <p>Price:{review.price}</p>
        <p>{review.write_up}</p>
        <p>Final Score: {review.final_score}</p>
        {review.user.id == userId ? <Link to={`/reviews/${review.id}/edit`}>Edit your review</Link> : ""}<br/>
        <Link to={`/users/${review.user.id}/reviews`} onClick={()=>getUserReviews(review.user)}> See all of {review.user.username}'s reviews</Link>


      </div> : <div> Maaaaan they ain't no review here </div>
  )

}

const mapStateToProps = (state, ownProps) => {
  const userId = state.currentUser ? state.currentUser.id : ""
  return ({
    userId,

  })
}

export default withRouter(connect(mapStateToProps, {getUserReviews})(ReviewCard))
