import React, {useState, useEffect } from 'react'
import {Link} from 'react-router-dom'
import { connect } from 'react-redux'
import { getConcerts, getConcert } from '../actions/concerts.js'
import { getConcertReviews } from '../actions/reviews.js'
import { withRouter } from 'react-router-dom'


const ConcertCard = ({theConcert, userId, getConcert, concertId }) => {
      const [concert, setConcert] = useState({});
      useEffect(() => {
          getConcert(concertId);
        },[concertId, getConcert]);
    return (
      concert ?
        <div className = "ConcertCard">
          <p>Date: {theConcert.date}</p>
          <p>Artist: {theConcert.artist}</p>
          <p>Venue: {theConcert.venue}</p>
          <p>Aggregated Score: {theConcert.combined_review_score}</p>
          <Link to ={`/concerts/${theConcert.id}/reviews`}> See all reviews for this concert</Link><br/>
          {!!theConcert.reviews.find(rev => rev.user_id == userId)} ? <Link to={`/concerts/${theConcert.id}/reviews/new`}>See your review</Link>:<Link to={`/concerts/${theConcert.id}/reviews/new`}>Add a review</Link>

          </div> : <div> Maaaaan they ain't no concert here </div>
      )

    }


const mapStateToProps = (state, ownProps) => {
  const userId = state.currentUser ? state.currentUser.id : ""
  const concertId = ownProps.match.params.id
  return ({
    userId,
    concertId,
    concerts: state.concerts,
    reviews: state.reviews,
    theConcert: state.concert



  })
}


export default withRouter(connect(mapStateToProps, {getConcerts, getConcertReviews, getConcert})(ConcertCard))
