import React from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { withRouter } from 'react-router-dom'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import { LinkContainer } from 'react-router-bootstrap';




class ReviewsList extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      likes: 0
     }

  }

  capitalize = (s) => {
  if (typeof s !== 'string') return ''
  return s.charAt(0).toUpperCase() + s.slice(1)
}

  handleClick = (event) => {
    console.log(this.state.likes)
    this.setState({
      likes: this.state.likes + 1
  })
  }

render () {
  return (
      <div className ='row'>
      {this.props.reviews.map(r =>
        <Card key={r.id} className="col-md-3" text="black">
          <Card.Body>
            <Card.Title>{this.capitalize(r.attributes.user.username)}'s review of {r.attributes.concert.name}</Card.Title>
            <Card.Text>Final Score: {r.attributes.final_score}</Card.Text>
            <LinkContainer to={`/reviews/${r.id}`}>
              <Button variant="success">See This Review</Button>
            </LinkContainer>
            <Button onClick={this.handleClick}> {this.state.likes} </Button>
          </Card.Body>
        </Card>)}
  </div>
 )

}

}




const mapStateToProps = (state, ownProps) => {
  return {

    reviews: state.reviews

  }
}

export default withRouter(connect(mapStateToProps)(ReviewsList))
