import React from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { withRouter } from 'react-router-dom'
import ConcertSearchForm from './ConcertSearchForm'




const ConcertsList = props => {


  const concertCards = props.concerts.length > 0 ?
    props.concerts.map(c => <><Link key={c.id} to={`/concerts/${c.id}`}>{c.lineup[0]} at {c.venue.name} on {c.datetime}</Link><br/></>)  : <p>Loading concerts</p>

  return {concertCards,
  }
}



const mapStateToProps = (state, ownProps) => {
  return {

      concerts: state.concerts

  }
}

export default withRouter(connect(mapStateToProps)(ConcertsList))
