import React from 'react';
import { connect } from 'react-redux'
import { updateConcertSearchForm } from '../actions/concertSearchForm'
import { withRouter } from 'react-router-dom'

const ConcertSearchForm = ({ artist, updateConcertSearchForm}) => {

  const handleChange = event => {
    const { name, value } = event.target
    updateConcertSearchForm(name, value)
  }
  return (
    <div className="concert-search-form" >
      <form onSubmit={props.handleSubmit}>
          <div className="artist-input">
            <input className='form-control' placeholder="Artist" type="text" name="artist" value={ props.artist } onChange={ props.handleChange }/>
          </div>
          <button className="search-button" type="submit">Search Concerts</button>
      </form>
    </div>
  )
}

export default ConcertSearchForm;
