import React from 'react'
import {Link} from 'react-router-dom'
import { connect } from 'react-redux'
import { getConcerts, saveConcert } from '../actions/concerts.js'
import { getConcertReviews } from '../actions/reviews.js'


class ConcertCard extends React.Component {




  render () {
    const { concert, userId, saveConcert } = this.props
    return (
      concert ?
        <div className = "ConcertCard">
          <p>Date: {concert.datetime}</p>
          <p>Artist: {concert.lineup[0]}</p>
          <p>Venue: {concert.venue.name}</p>
          <p>Aggregated Score: </p>
          <Link to ={`/concerts/${concert.id}/reviews`}> See all reviews for this concert</Link><br/>
          {!!concert.reviews.find(rev => rev.attributes.user.id == userId) ? <Link to={`/reviews/${concert.reviews.find(rev => rev.user_id == userId).id}`}>Your Review</Link>: <Link to={`/concerts/${concert.id}/reviews/new`} onClick={()=>saveConcert(concert)}>Add a review</Link> }


          </div> : <div> Maaaaan they ain't no concert here </div>
      )

    }
  }

const mapStateToProps = state => {
  const userId = state.currentUser ? state.currentUser.id : ""
  return ({
    userId,
    concerts: state.concerts

  })
}


export default connect(mapStateToProps, {getConcerts, getConcertReviews, saveConcert})(ConcertCard)
