import React from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { withRouter } from 'react-router-dom'
import { saveConcert } from '../.././actions/concerts/concerts'
import Card from 'react-bootstrap/Card'
import Container from 'react-bootstrap/Container'
import { LinkContainer } from 'react-router-bootstrap';
import LoadingBar from 'react-redux-loading-bar';


const ConcertsList = ({concerts, saveConcert, isLoading}) => {

    const date = (datetime) => {
      return new Date(datetime).toDateString()
    }
    return (
       isLoading ? <LoadingBar/> :

            <div className ='row'>
              {concerts.sort((a, b) => (a.datetime < b.datetime) ? 1 : -1).map(
                c =>
                  <Card key={c.id} className="col-md-3" bg="primary" text="black">
                    <Card.Body>
                      <Card.Title>{c.lineup[0]} at {c.venue.name} on {date(c.datetime)}</Card.Title>
                      <LinkContainer to={`/concerts/${c.id}`} onClick={()=>saveConcert(c)}>
                        <button>See This Concert</button>
                      </LinkContainer>
                    </Card.Body>
                  </Card>)}
              </div>



    )
}

const mapStateToProps = (state) => {

  return {

      isLoading: state.concerts.isLoading

  }
}



export default withRouter(connect(mapStateToProps, {saveConcert})(ConcertsList))
