import React from 'react'
import { connect } from 'react-redux'
import { updateLogin } from '../actions/login.js'
import { login } from '../actions/currentUser.js'
import Jumbotron from 'react-bootstrap/Jumbotron';
import Button from 'react-bootstrap/Button';

const Login = ({ loginData, updateLogin, login, history }) => {

  const handleChange = event => {
    const { name, value } = event.target
    const updatedInfo = {
      ...loginData,
      [name]: value
    }
    updateLogin(updatedInfo)
  }

  const handleSubmit = event => {
    event.preventDefault()
    login(loginData, history)
  }
  return (
    <Jumbotron>
      <form className="form form__login" onSubmit={handleSubmit}>
        <input className="form form__input" placeholder="username" value={loginData.username} name="username" type="text" onChange={handleChange}/>
        <input className="form form__input" placeholder="password" value={loginData.password} name="password" type="password" onChange={handleChange}/>
        <input className = "form form__submit" type ="submit" value = "Log In"/>
      </form>
    </Jumbotron>
  )
}

const mapStateToProps = state => {
  return {
    loginData: state.login
  }
}


export default connect(mapStateToProps, { updateLogin, login })(Login)
